sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDateToday: function(strDate){
		// Get the date only of rightnow and whenNeeded without timestamp
		var dateToday = new GlideDateTime();
		var dateWhenNeeded = new GlideDateTime(strDate);

		var localDateToday = dateToday.getLocalDate();
		var localDateWhenNeeded = dateWhenNeeded.getLocalDate();

		// If the testdate is before rightnow, return true, else return false
		if (localDateToday.compareTo(localDateWhenNeeded) == 0) {
			return true;
		}
		else {
			return false;
		}
	},

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	type: 'NeedItUtils'
};
